$(function () {
  $('#menu-list [href]').each(function () {
    if (this.href == window.location.href) {
      $(this).addClass('underline ');
    }
  });
  $('#menu-button').click(function () {
    $('#menu-list').toggleClass('navbar__list--open');
  });
  $('#name-error').hide();
  let nameError = !0;
  $('#name').keyup(() => {
    validateName();
  });
  const validateName = () => {
    let name = $('#name').val();
    if (name === '') {
      $('#name-error').show();
      nameError = !1;
      return !1;
    } else {
      $('#name-error').hide();
    }
  };
  $('#email-error').hide();
  let emailError = !0;
  $('#email').keyup(() => {
    validateEmail();
  });
  const validateEmail = () => {
    let email = $('#email').val();
    let regex = /^([_\-\.0-9a-zA-Z]+)@([_\-\.0-9a-zA-Z]+)\.([a-zA-Z]){2,7}$/;
    if (email === '') {
      $('#email-error').show();
      emailError = !1;
      return !1;
    } else if (regex.test(email) === !1) {
      $('#email-error').show();
      emailError = !1;
      return !1;
    } else {
      $('#email-error').hide();
    }
  };
  $('#submit').on('click', (element) => {
    validateName();
    validateEmail();
    if (nameError === !0 && emailError === !0) {
      return !0;
    } else {
      return !1;
    }
  });
});
